if (status === 'close' && killed === false) {
    return { action: { command: 'start' }, delay: 5000 };
}

return;

const props = msg.payload.toString().split('\n');

const progress = {};

props.forEach(item => {

    const [name, value] = item.split('=');

    if (name && value) {

        progress[name] = value;

    }

});

const color = progress['progress'] === 'continue' ? 'green' : 'red';

const message = `fps: ${progress['fps']}, bitrate: ${progress['bitrate']}`;

node.status({ fill: color, shape: 'dot', text: message });

return { payload: progress };

